mapper.select:
select count(1) from t_fee f,t_client c where f.client_id=c.id
and f.client_id=#{client}
and (f.bill_code like concat('%',#{key},'%') or c.cname like concat('%',#{key},'%') or f.fname like concat('%',#{key},'%'))
and f.edate BETWEEN #{sdate} and #{edate}

select f.id, f.bill_id, f.bill_code,f.client_id, f.ftype,f.fname,f.price,f.amount, f.sdate, f.edate, f.memo,f.flag,f.op,f.cr_date,f.cr_date,c.cname from t_fee f,t_client c where f.client_id=c.id
and f.client_id=#{client}
and (f.bill_code like concat('%',#{key},'%') or c.cname like concat('%',#{key},'%') or f.fname like concat('%',#{key},'%'))
and f.edate BETWEEN #{sdate} and #{edate}
order by #{page.sort} #{page.order}
limit #{page.start},#{page.end}

select
count(1) from t_fee f,t_client c,t_delivery d
where
f.client_id=c.id
and f.bill_id=d.id

	and f.flag=#{fflag}


	and d.flag=#{bflag}


	and f.client_id=#{client}


	and (f.bill_code like concat('%',#{key},'%') or c.cname
	like concat('%',#{key},'%') or
	f.fname like
	concat('%',#{key},'%'))


	and f.edate BETWEEN #{sdate} and #{edate}

select f.id, f.bill_id,f.fname,f.amount,f.sdate, f.edate,
f.memo,f.op,f.cr_date,c.cname,d.code,d.order_code,d.case_model,
d.address,d.start_port,d.end_port,d.dport,d.rport,d.case_no,d.seal_no
from
t_fee f,t_client c,t_delivery d
where
f.client_id=c.id and
f.bill_id=d.id

	and f.flag=#{fflag}


	and d.flag=#{bflag}


	and f.client_id=#{client}


	and (f.bill_code like concat('%',#{key},'%') or c.cname
	like concat('%',#{key},'%') or
	f.fname like
	concat('%',#{key},'%'))


	and f.edate BETWEEN #{sdate} and #{edate}


	order by #{page.sort} #{page.order}


	limit #{page.start},#{page.end}

select
count(1)from t_fee f,t_client c,t_stock_in s
where
f.client_id=c.id
and f.bill_id=s.id

	and f.flag=#{fflag}


	and s.flag=#{bflag}


	and f.client_id=#{client}


	and (f.bill_code like concat('%',#{key},'%') or c.cname
	like concat('%',#{key},'%') or
	f.fname like
	concat('%',#{key},'%'))


	and f.edate BETWEEN #{sdate} and #{edate}

select f.id, f.bill_id,f.fname,f.amount,f.sdate, f.edate,
f.memo,f.op,f.cr_date,c.cname,s.code,s.order_code,s.car_no,s.indate,s.out_date
from
t_fee f,t_client c,t_stock_in s
where
f.client_id=c.id and
f.bill_id=s.id

	and f.flag=#{fflag}


	and s.flag=#{bflag}


	and f.client_id=#{client}


	and (f.bill_code like concat('%',#{key},'%') or c.cname
	like concat('%',#{key},'%') or
	f.fname like
	concat('%',#{key},'%'))


	and f.edate BETWEEN #{sdate} and #{edate}


	order by #{page.sort} #{page.order}


	limit #{page.start},#{page.end}

select
count(1)
from t_bill b,t_client c where
b.client_id=c.id

	and b.client_id=#{client}


	and b.edate BETWEEN #{sdate} and #{edate}

select b.id,b.code,b.sdate,b.edate,b.total,b.memo,c.cname
from t_bill
b,t_client c where
b.client_id=c.id

	and b.client_id=#{client}


	and b.edate BETWEEN #{sdate} and #{edate}


	order by #{page.sort} #{page.order}


	limit #{page.start},#{page.end}
